import React, { Component } from "react";
import Slider from "react-slick";
import $ from 'jquery';

import './Carousel.scss';

export default class SimpleSlider extends Component {

  constructor(props) {
    super(props);
    this.state = { collection: props.collection,
                    anchorId: props.anchorId,
                    slideToIndex: props.slideNum };


  }

  componentDidMount() {

    if (window.location.hash.includes(this.state.anchorId)) {

      this.slider.slickGoTo(this.state.slideToIndex);

    }

  }

  render() {
    const settings = {
      dots: false,
      infinite: false,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      prevArrow: <img src="https://furthermore-cdn.equinox.com/source-of-strength/static/media/arrow_left.png"/>,
      nextArrow: <img src="https://furthermore-cdn.equinox.com/source-of-strength/static/media/arrow_right.png"/>
    };

    return (
      <div className="Carousel" id={ this.state.anchorId}>

        <Slider ref={slider => (this.slider = slider)} {...settings} >
          {this.state.collection.map(function(imgSrc, i){
            return (
              <div key={"a" + i}>
                <img key={"b" + i} className="carousel-img" src="https://furthermore-cdn.equinox.com/source-of-strength/static/media/test.png"/>
                <img key={"c" + i} className="carousel-ad-desktop" src="https://furthermore-cdn.equinox.com/source-of-strength/static/media/ad-img-2.jpg"/>
                <img key={"d" + i} className="carousel-ad-mobile" src="https://furthermore-cdn.equinox.com/source-of-strength/static/media/ad-img-mobile.jpg"/>

              </div>);
          })}
        </Slider>

      </div>
    );
  }
}
