import React from 'react'
import './App.scss';
import $ from "jquery";
import Intro from "./Intro.js";
import Navbar from './Navbar.js'
import TalentModule from './common-components/TalentModule.js'
import Video from './common-components/Video.js'
import Ad from './common-components/Ad.js'
import IntroSubcopy from './IntroSubcopy.js'
import MiniAd from './MiniAd.js'
import Anchors from './Anchors.js'


class App extends React.Component {

  constructor(props) {
    super(props);

    if (window.location.search) {
      this.state = {slideNum: parseInt(window.location.search.slice(1))}
    } else {
      this.state = {slideNum: 0}

    }

  }

  componentDidMount() {
    var hash = window.location.hash;
    if (hash !== '') {

      setTimeout(() => {
        const id = hash.replace('#', '');
        const element = document.getElementById(id);
        if (element) element.scrollIntoView({block:"center", behavior:"smooth"});
      }, 0);
    }
  }

  render() {

    var beeCollection = ["A", "B", "C"],
        joshCollection = ["D", "E", "F"],
        danielleCollection = ["G", "H", "F"];

        //video assets here

    return (
      <div className="App">
        <Navbar />
        <Intro />
        <Video video="https://furthermore-cdn.equinox.com/source-of-strength/static/media/main.mp4" />
        <Anchors />
        <IntroSubcopy />
        <MiniAd />

        <TalentModule
          id="beesection"
          headshot="https://furthermore-cdn.equinox.com/source-of-strength/static/media/bee-retouch.jpg"
          name="Bee Bosnak: "
          heading="yoga and meditation"
          subcopy="Bee is a yoga and meditation teacher in New York City. “I got started with these two practices almost a decade ago when I was lost,” says Bosnak. The combination helped to heal her and to ultimately change her life for the better."
          secondImage="https://furthermore-cdn.equinox.com/source-of-strength/static/media/bee-main-retouch.jpg"
          collection={beeCollection}
          video="https://furthermore-cdn.equinox.com/source-of-strength/static/media/Bee.mp4"
          anchorId="beevideos"
          subcopyId="0"
          slideNum={this.state.slideNum}
         />

        <Ad />

        <TalentModule
          id="joshsection"
          headshot="https://furthermore-cdn.equinox.com/source-of-strength/static/media/josh-port-retouch.jpg"
          name="Josh Dixon: "
          heading="community engagment"
          subcopy="Josh lives in Newport Beach, California. He’s a tech entrepreneur and former Team USA gymnast. But beyond his day job, he knows the value of giving back: “I’ve been very involved in non-profit LGBT advocacy, adoption and family planning equality initiatives (myself and my sisters were all adopted), Boys and Girls Club of America, and Second Harvest Food Bank,” says Dixon, who also started scholarships for his middle school track and field program and a college scholarship for seniors from his high school."
          secondImage="https://furthermore-cdn.equinox.com/source-of-strength/static/media/josh-main-retouch.jpg"
          collection={joshCollection}
          video="https://furthermore-cdn.equinox.com/source-of-strength/static/media/Josh.mp4"
          anchorId="joshvideos"
          subcopyId="1"
          slideNum={this.state.slideNum}

        />

        <Ad />

        <TalentModule
          id="daniellesection"
          headshot="https://furthermore-cdn.equinox.com/source-of-strength/static/media/Danielle-headshot.jpg"
          name="Danielle Gray: "
          heading="solo travel"
          subcopy="Danielle is a Los Angeles-based Tier 3+ trainer at Equinox, a gymnast, and a fitness model. For her, strength comes from stretching outside of her comfort zone. In particular, it comes from traveling by herself. “Getting lost, problem-solving, planning itineraries, and meeting transportation schedules all builds confidence in our independence,” says Gray. “We feel free which therefore makes us feel strong when we can people-watch at coffee shops, travel at our own pace, do things we want to do and be completely present.”"
          secondImage="https://furthermore-cdn.equinox.com/source-of-strength/static/media/danielle-secondaryImage.png"
          collection={danielleCollection}
          video="https://furthermore-cdn.equinox.com/source-of-strength/static/media/Danielle.mp4"
          anchorId="daniellevideos"
          subcopyId="2"
          slideNum={this.state.slideNum}

         />
         <div className="black-background-padding"></div>

      </div>
    )
  }
}
export default App
